import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import ReviewCard from "./ReviewCard";
import NavBar from "./NavBar";
import "./styles/MovieDetails.css";

function MovieDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState({});
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState("");
  const [averageRating, setAverageRating] = useState(null);
  const email = useSelector((state) => state.user.email);

  useEffect(() => {
    console.log(email);
    //fetch movie details
    fetch(`http://localhost:8080/movie/${id}`)
      .then((res) => res.json())
      .then((data) => setMovie(data));

    //fetch reviews
    fetch(`http://localhost:8080/reviews/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        if (Array.isArray(data)) {
          setReviews(data);
        } else {
          setReviews([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching reviews:", error);
        setReviews([]);
      });

    fetch(`http://localhost:8080/movie/${id}/average-rating`)
      .then((response) => response.json())
      .then((data) => {
        if (data.averageRating === null) {
          setAverageRating("-/5");
        } else {
          setAverageRating(data.averageRating.toFixed(2) + "/5");
        }
      })
      .catch((error) => {
        console.error("Error fetching average rating:", error);
        setAverageRating("-/5");
      });
  }, [id, email]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      user: email,
      movie: id,
      rating,
      reviewText,
    };
    console.log("Payload:", payload);
    console.log(email);
    await fetch(`http://localhost:8080/review`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ user: email, movie: id, rating, reviewText }),
    })
      .then((res) => res.json())
      .then((data) => setReviews([...reviews, data]));

    setRating(0);
    setReviewText("");
  };

  const handleUpvote = async (reviewId) => {
    await fetch(`http://localhost:8080/review/upvote/${reviewId}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((updatedReview) => {
        setReviews(
          reviews.map((review) =>
            review._id === updatedReview._id ? updatedReview : review
          )
        );
      });
  };

  const handleRemoveUpvote = async (reviewId) => {
    await fetch(`http://localhost:8080/review/remove-upvote/${reviewId}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((updatedReview) => {
        setReviews(
          reviews.map((review) =>
            review._id === updatedReview._id ? updatedReview : review
          )
        );
      });
  };

  return (
    <div className="movie-details-container">
      <NavBar />
      <Button onClick={() => navigate(-1)} className="mb-3">
        Back
      </Button>

      <div className="movie-header">
        <img src={movie.picture} alt={movie.title} className="movie-image" />
        <div className="movie-info">
          <h1 className="movie-title">{movie.title}</h1>
          <p className="movie-overview">{movie.overview}</p>
          <p>Release Date: {movie.release_date}</p>
          <p className="movie-rating">Average Rating: {averageRating}</p>
        </div>
      </div>

      <h2>Add a Review</h2>
      <Form onSubmit={handleReviewSubmit} className="review-form">
        <Form.Group>
          <Form.Label>Rating</Form.Label>
          <Form.Control
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="5"
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Review</Form.Label>
          <Form.Control
            as="textarea"
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
          />
        </Form.Group>
        <Button type="submit" className="mt-3">
          Submit
        </Button>
      </Form>

      <h2 className="review-section">Reviews</h2>
      {reviews.map((review) => (
        <div key={review._id} className="review-card-container">
          <ReviewCard
            review={review}
            onUpvote={handleUpvote}
            onRemoveUpvote={handleRemoveUpvote}
          />
        </div>
      ))}
    </div>
  );
}
