import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const MovieDetails = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [rating, setRating] = useState(0);
  const [reviewText, setReviewText] = useState("");

  const userId = useSelector((state) => state.user.userid); // Get user ID from Redux store

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const response = await fetch(`http://localhost:4000/movie/${id}`);
        const data = await response.json();
        setMovie(data);
      } catch (error) {
        console.error("Error fetching movie:", error);
      }
    };

    fetchMovie();
  }, [id]);

  const handleSubmit = async () => {
    const reviewData = {
      userId: userId,
      movieId: movie._id,
      rating: rating,
      reviewText: reviewText,
    };

    try {
      const response = await fetch("http://localhost:4000/review", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reviewData),
      });

      if (response.ok) {
        const data = await response.json();
        setMovie((prevState) => ({
          ...prevState,
          reviews: [...prevState.reviews, data],
        }));
        setRating(0);
        setReviewText("");
      } else {
        console.error("Failed to submit review");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleBack = () => {
    history.goBack();
  };

  if (!movie) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{movie.title}</h1>
      <p>{movie.overview}</p>
      <p>Year: {movie.year}</p>
      <p>Director: {movie.director}</p>
      <p>Cast: {movie.cast.join(", ")}</p>
      {/* Add more movie details as needed */}

      <h2>Reviews</h2>
      {movie.reviews.map((review, index) => (
        <div key={index}>
          <p>Rating: {review.rating}</p>
          <p>Review: {review.reviewText}</p>
        </div>
      ))}

      <h2>Add a Review</h2>
      <label>
        Rating:
        <input
          type="number"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          min="1"
          max="5"
        />
      </label>
      <br />
      <label>
        Review:
        <textarea
          value={reviewText}
          onChange={(e) => setReviewText(e.target.value)}
        />
      </label>
      <br />
      <button onClick={handleSubmit}>Submit Review</button>
      <br />
      <button onClick={handleBack}>Back</button>
    </div>
  );
};

export default MovieDetails;
