import React from 'react'
import { useParams } from 'react-router-dom'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useState } from 'react';

function Otp() {
    const [otp, setOtp] = useState('')
    const [correctOtp, setcorrectOtp] = useState("none")

    const id = useParams().id
    const otpHandler = (e)=>{
        e.preventDefault()
        try{
          fetch("http://localhost:5000/checkOtp", {
              method: "POST", 
              headers: {
                  'Access-Control-Allow-Origin':true,
                  'Content-Type': 'application/json',
              },
              body: JSON.stringify({
                  id:id,
                  otp:otp,
              })
          })
          .then((res)=>{
              return res.json()
          })
          .then((res)=>{ 
              if(res.result){
                alert(res.message)
              }
              else{
                setcorrectOtp("")
              }
          })
      }
      catch(err){
          console.log(err)
      }
      } 
    
  return (
    <div>
    <div className='formparent' style={{display:correctOtp}}>
    <div className='otpform'>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicOTP">
          <Form.Label>Enter your OTP: </Form.Label>
          <Form.Control type="text" placeholder="Enter your OTP" onChange={(e)=>{setOtp(e.target.value)}} style={{width:"300px"}}/>
        </Form.Group>

        <Button variant="primary" type="submit" onClick={(e)=>{otpHandler(e)}} style={{width:"300px"}}>
          Check OTP
        </Button>
    </Form>
    </div>
    </div>
    </div>
  )
}

export default Otp
